<script>
import Btn from '@/components/Btn'
import IngredientsForm from '@/components/IngredientsForm'
import PaginationDots from '@/components/PaginationDots'

export default {
  name: 'OnboardingStep',
  components: {
    Btn,
    IngredientsForm,
    PaginationDots
  },
  data: () => ({
    totalIngredients: 0
  }),
  computed: {
    onboardingStep () {
      return parseFloat(this.$route.params.step)
    }
  },
  methods: {
    handleTotalIngredients (total) {
      this.totalIngredients = total
    },
    handleNext () {
      const nextStep = this.onboardingStep + 1

      if (this.onboardingStep === 3) {
        this.$router.push({
          path: '/'
        })
      } else {
        this.$router.push({
          path: `/onboarding/${nextStep}`
        })
      }
    }
  }
}
</script>

